onClipEvent (load) {
	if (_parent._name == _root.控制目标)
	{
		_parent.读取当前飞行状态();
		// 按下攻击键K后触发
		if (!_parent.飞行浮空 && _parent.被动技能.上挑 && _parent.被动技能.上挑.启用 && Key.isDown(_parent.B键))
		{
			_parent.跳横移速度 = _parent.行走X速度;
			_parent.跳跃中移动速度 = _parent.行走X速度;
			_parent.状态改变("兵器跳");
		}
	}
	this.onUnload = function()
	{
		_parent.UpdateBigSmallState("普攻结束","兵器攻击结束");
	};
}

onClipEvent (load) {
	if (_parent._name == _root.控制目标)
	{
		_parent.读取当前飞行状态();
		// 按下攻击键K后触发
		if (_parent.被动技能.上挑 && _parent.被动技能.上挑.启用 && Key.isDown(_parent.B键))
		{
			_parent.跳横移速度 = _parent.跑X速度;
			_parent.跳跃中移动速度 = _parent.跑X速度;
			_parent.状态改变("兵器跳");
		}
	}
	this.onUnload = function()
	{
		_parent.UpdateBigSmallState("冲击结束","兵器冲击结束");
	};
}

onClipEvent (load) {
	function 死()
	{
		_parent.死亡检测();
	}
	if (_parent._name == _root.控制目标)
	{
		_parent.读取当前飞行状态();
	}
	_parent._y = _parent.Z轴坐标;
	_parent.倒地 = true;
	_parent.格斗架势 = true;
	_root.主角函数.跳转到招式(this, "a", 6);
	this.onUnload = function()
	{
		_parent.倒地 = false;
	};
}

onClipEvent (enterFrame) {
	if(_parent.移动射击倒退){
		if(this._currentframe == 1){
			gotoAndStop("end");
		}else{
			gotoAndStop(this._currentframe-1);
		}
	}else{
		play();
	}
}

onClipEvent (enterFrame) {
	if(_parent.移动射击倒退){
		if(this._currentframe == 1){
			gotoAndStop("end");
		}else{
			gotoAndStop(this._currentframe-1);
		}
	}else{
		play();
	}
}

onClipEvent (enterFrame) {
	if(_parent.移动射击倒退){
		if(this._currentframe == 1){
			gotoAndStop("end");
		}else{
			gotoAndStop(this._currentframe-1);
		}
	}else{
		play();
	}
}

