Actionscript.script:
function 射击()
{
   if(_parent.手枪射击次数 < 弹夹容量)
   {
      if(枪_扣扳机 == true)
      {
         _parent.手枪射击次数++;
         if(_root.控制目标 == _parent._name)
         {
            _root.玩家信息界面.玩家必要信息界面.子弹数 = 弹夹容量 - _parent.手枪射击次数;
         }
         _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,_parent._name,shootX,shootY,_parent.Z轴坐标,子弹敌我属性,击倒率,击中后子弹的效果);

         if(手枪是否单发 == true)
         {
            _parent.单发枪计时();
         }
         if(Key.isDown(_parent.下键))
         {
            if(射击方向传值 != 1)
            {
               射击方向传值 = 1;
               联机_是否射击 = true;
            }
            gotoAndStop("下射击");
            play();
         }
         else if(Key.isDown(_parent.上键))
         {
            if(射击方向传值 != 2)
            {
               射击方向传值 = 2;
               联机_是否射击 = true;
            }
            gotoAndStop("上射击");
            play();
         }
         else
         {
            if(射击方向传值 != 0)
            {
               射击方向传值 = 0;
               联机_是否射击 = true;
            }
            gotoAndStop("射击");
            play();
            if(_parent.联机_是否射击)
            {
               _root.client.sendData(DataPackage.射击(射击方向传值));
               联机_是否射击 = false;
            }
         }
      }
   }
}
function 射击_2()
{
   if(_parent.手枪2射击次数 < 弹夹容量_2)
   {
      if(枪2_扣扳机 == true)
      {
         _parent.手枪2射击次数++;
         if(_root.控制目标 == _parent._name)
         {
            _root.玩家信息界面.玩家必要信息界面.子弹数_2 = 弹夹容量_2 - _parent.手枪2射击次数;
         }
         _root.子弹区域shoot(声音_2,霰弹值_2,子弹散射度_2,发射效果_2,子弹种类_2,子弹威力_2,子弹速度_2,Z轴攻击范围_2,击中地图效果_2,_parent._name,shootX_2,shootY_2,_parent.Z轴坐标,子弹敌我属性,击倒率_2,击中后子弹的效果_2);

         if(手枪2是否单发_2 == true)
         {
            _parent.单发枪计时_2();
         }
         if(_root.控制目标全自动 == false and _root.控制目标 == _parent._name)
         {
            if(Key.isDown(_root.下键))
            {
               gotoAndStop("下射击2");
               play();
            }
            else if(Key.isDown(_root.上键))
            {
               gotoAndStop("上射击2");
               play();
            }
            else
            {
               gotoAndStop("射击2");
               play();
            }
         }
      }
   }
}

function 换弹夹()
{
   delete this.onEnterFrame;
   clearInterval(keepshooting);
   clearInterval(keepshooting_2);
   if(_root.控制目标 == _parent._name)
   {
      枪可换弹夹 = false;
      枪2可换弹夹 = false;
      var _loc4_ = 0;
      while(_loc4_ < _root.物品栏总数)
      {
         if(_root.物品栏[_loc4_][0] == 使用弹夹名称)
         {
            if(_root.物品栏[_loc4_][1] >= 1)
            {
               枪弹夹值 = _loc4_;
               枪可换弹夹 = true;
            }
            else
            {
               _root.发布消息("一只枪弹夹耗尽！");
               _root.物品栏[_loc4_] = ["空",0,0];
               _root.排列物品图标();
            }
         }
         if(_root.物品栏[_loc4_][0] == 使用弹夹名称_2)
         {
            if(_root.物品栏[_loc4_][1] >= 1)
            {
               枪2弹夹值 = _loc4_;
               枪2可换弹夹 = true;
            }
            else
            {
               _root.发布消息("另一只枪弹夹耗尽！");
               _root.物品栏[_loc4_] = ["空",0,0];
               _root.排列物品图标();
            }
         }
         if(枪可换弹夹 == true and 枪2可换弹夹 == true)
         {
            break;
         }
         _loc4_ += 1;
      }
      if(枪可换弹夹 == true or 枪2可换弹夹 == true)
      {
         gotoAndStop("换弹夹");
         play();
      }
      else if(枪可换弹夹 == false and 枪2可换弹夹 == false and _parent.手枪射击次数 >= 弹夹容量 and _parent.手枪2射击次数 >= 弹夹容量_2)
      {
         gotoAndStop("空枪");
         play();
      }
   }
   else
   {
      _parent.手枪射击次数 = 0;
      _parent.手枪2射击次数 = 0;
      gotoAndStop("换弹夹");
      play();
   }
}
function 检查下是否有弹夹(弹夹名)
{
   var _loc3_ = 0;
   while(_loc3_ < _root.物品栏总数)
   {
      if(_root.物品栏[_loc3_][0] == 弹夹名)
      {
         return _root.物品栏[_loc3_][1];
      }
      _loc3_ += 1;
   }
   return 0;
}
