if(剩余弹夹数 <= 0 and 剩余弹夹数_2 <= 0 and _parent.手枪射击次数 >= 弹夹容量 and _parent.手枪2射击次数 >= 弹夹容量_2 and _parent.强制换弹夹 != true)
{
   delete this.onEnterFrame;
   clearInterval(keepshooting);
   clearInterval(keepshooting_2);
   gotoAndStop("空枪");
   play();
}
else
{
   if(剩余弹夹数 >= 0 and _parent.手枪射击次数 < 弹夹容量 and _parent.强制换弹夹 != true)
   {
      if(手枪是否单发 == true)
      {
         if(_parent.单发枪计时_时间结束 == true)
         {
            射击();
         }
      }
      else
      {
         射击();
      }
      keepshooting = setInterval(射击,射击速度);
   }
   if(剩余弹夹数_2 >= 0 and _parent.手枪2射击次数 < 弹夹容量_2 and _parent.强制换弹夹 != true)
   {
      if(手枪2是否单发_2 == true)
      {
         if(_parent.单发枪计时_时间结束_2 == true)
         {
            射击_2();
         }
      }
      else
      {
         射击_2();
      }
      keepshooting_2 = setInterval(射击_2,射击速度_2);
   }
}
结束攻击计数 = 0;
结束攻击数 = random(200);
this.onEnterFrame = function()
{
   if(_parent.手枪射击次数 >= 弹夹容量 and _parent.手枪2射击次数 >= 弹夹容量_2)
   {
      换弹夹();
   }
   if(_parent.操控编号 != -1 and _root.控制目标全自动 == false and Key.isDown(_parent.A键) == false and Key.isDown(_parent.B键) == false)
   {
      clearInterval(keepshooting);
      clearInterval(keepshooting_2);
      gotoAndStop("结束");
   }
   结束攻击计数++;
   if(结束攻击数 < 结束攻击计数)
   {
      _parent.动画完毕();
   }
};
this.onUnload = function()
{
   clearInterval(keepshooting);
   clearInterval(keepshooting_2);
   delete this.onEnterFrame;
};
stop();

