onClipEvent(load){
   扳机键 = "B键";
   控制参数 = "枪2_扣扳机";
   if(_parent._parent.操控编号 != -1 and _root.控制目标全自动 == false)
   {
      if(Key.isDown(_parent._parent[扳机键]) == true)
      {
         gotoAndStop("已扣扳机");
      }
      else
      {
         gotoAndStop("没扣扳机");
      }
   }
   else
   {
      gotoAndStop("失效");
   }
}

onClipEvent(load){
   扳机键 = "A键";
   控制参数 = "枪_扣扳机";
   if(_parent._parent.操控编号 != -1 and _root.控制目标全自动 == false)
   {
      if(Key.isDown(_parent._parent[扳机键]) == true)
      {
         gotoAndStop("已扣扳机");
      }
      else
      {
         gotoAndStop("没扣扳机");
      }
   }
   else
   {
      gotoAndStop("失效");
   }
}

onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}
