Actionscript.script:
_parent.UpdateSmallState("十六夜月华开始");

//初始化计数或检查上限
//if (十六夜月华次数 == "undefined")
if (typeof 十六夜月华次数 == "undefined")
{
	// ← 首次进入时初始化为1
	var 十六夜月华次数 = 1;
}
else if (十六夜月华次数 >= 3)
{
	_root.兵器攻击路由.动画完毕(this,_parent);

}
else
{
	十六夜月华次数 += 1;
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.十六夜月华可派生();
		var 按后 = (自机.方向 == "右") ? 自机.左行 : 自机.右行;
		var 按前 = (自机.方向 == "右") ? 自机.右行 : 自机.左行;
		if (按后 && !按前 && 自机.动作A && _parent.十六夜月华次数 <= 3)
		{
			//_root.发布消息("十六夜月华次数" + _parent.十六夜月华次数);
			_parent.gotoAndPlay("十六夜月华");
		}
	}
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);
	自机 = _parent._parent;
	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  自机.空手攻击力 / 5 + 自机.刀属性.power * 1;
	if (自机.mp攻击加成 && 自机.等级 >= 10)
	{子弹属性.子弹威力 += 自机.mp攻击加成 * 1;}
	if(自机.被动技能.刀剑攻击 && 自机.被动技能.刀剑攻击.等级 && 自机.等级 >= 15)
	{
		子弹属性.子弹威力 +=  自机.刀属性.power *  自机.被动技能.刀剑攻击.等级 / 13;
	}
	if(自机.被动技能.上挑 && 自机.被动技能.上挑.等级 && 自机.等级 >= 15)
	{
		子弹属性.子弹威力 +=  自机.刀属性.power *  自机.被动技能.上挑.等级 / 10;
	}
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 50;
	子弹属性.不硬直 = true;
	子弹属性.水平击退速度 = 12;
	子弹属性.击中后子弹的效果 = "斩打命中特效";

	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);
	自机 = _parent._parent;
	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  自机.空手攻击力 / 5 + 自机.刀属性.power * 1;
