	{子弹属性.子弹威力 += 自机.mp攻击加成 * 1;}
	if(自机.被动技能.刀剑攻击 && 自机.被动技能.刀剑攻击.等级 && 自机.等级 >= 15)
	{
		子弹属性.子弹威力 +=  自机.刀属性.power *  自机.被动技能.刀剑攻击.等级 / 13;
	}
	if(自机.被动技能.上挑 && 自机.被动技能.上挑.等级 && 自机.等级 >= 15)
	{
		子弹属性.子弹威力 +=  自机.刀属性.power *  自机.被动技能.上挑.等级 / 10;
