	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 50;
	子弹属性.不硬直 = true;
	子弹属性.水平击退速度 = 12;
	子弹属性.击中后子弹的效果 = "斩打命中特效";

	//非常用
