Actionscript.script:
X轴追踪移动 = _root.敌人二级函数.X轴追踪移动;
Z轴追踪移动 = _root.敌人二级函数.Z轴追踪移动;
_parent.UpdateSmallState("次元斩开始");

var 索敌距离 = 550;
var 是否强行重新索敌 = true;
var 优先索敌属性 = "距离";
var 优先索敌排序 = "顺序";
var 是否追踪 = true;
var 敌人顺序 = 0;
if(_root.控制目标 == _parent._name){
	//if(Key.isDown(_root.武器变形键)){
		//是否强行重新索敌 = true;
	//}
	if(Key.isDown(_root.互动键)){
		优先索敌属性 = "血量";
		优先索敌排序 = "倒序";
	}
	if(Key.isDown(_root.奔跑键)){
		是否追踪 = false;
	}
}
var 追踪目标 = _parent.按距离索敌(索敌距离, 是否强行重新索敌, 优先索敌属性, 优先索敌排序);
if(!追踪目标){
	是否追踪 = false;
}
/*
shootX = _root.gameworld[_parent.追踪目标]._x;
shootY = _root.gameworld[_parent.追踪目标]._y;
shootZ = _root.gameworld[_parent.追踪目标].Z轴坐标;
*/
shootX = 追踪目标._x;
shootY = 追踪目标._y;
shootZ = 追踪目标.Z轴坐标;

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.次元斩可派生();
		if (自机.动作C)
		{
			自机.动画完毕();
		}
	}
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.次元斩可派生();
		if (自机.动作C)
		{
			//_root.发布消息("尝试出次元斩");
			_parent.gotoAndPlay("次元斩");
		}
	}
}

onClipEvent(load){
	自机 = _parent._parent;
   声音 = "";
   霰弹值 = 1;
   子弹散射度 = 0;
   发射效果 = "";
   子弹种类 = "JK次元斩";
   子弹威力 = 自机.空手攻击力 / 5 + 自机.内力 * 1 + 自机.刀属性.power * 1;
   /*
   子弹威力 = _parent._parent.空手攻击力 * 1.5;
   if(_parent.敌方防御){
	   子弹威力 += _parent.敌方防御 * 3.5;
   }
   */
   子弹速度 = 0;
   击中地图效果 = "";
   Z轴攻击范围 = 70;
   击倒率 = 1;
   击中后子弹的效果 = "斩打命中特效";
   if(_parent._parent.是否为敌人 == true)
   {
      子弹敌我属性 = false;
   }
   else
   {
      子弹敌我属性 = true;
   }
   var myPoint = {x:this._x,y:this._y};
   _parent.localToGlobal(myPoint);
   _root.gameworld.globalToLocal(myPoint);
   /*
   shootX = myPoint.x;
   shootY = myPoint.y;
   */
   shootX = _parent.shootX;
   shootY = _parent.shootY;
   shootZ = _parent.shootZ;

   _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,_parent._parent._name,shootX,shootY,shootZ,子弹敌我属性,击倒率,击中后子弹的效果);
}

onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}
