Actionscript.script:
X轴追踪移动 = _root.敌人二级函数.X轴追踪移动;
Z轴追踪移动 = _root.敌人二级函数.Z轴追踪移动;
_parent.UpdateSmallState("猎影十字开始");

var 索敌距离 = 550;
var 是否强行重新索敌 = true;
var 优先索敌属性 = "距离";
var 优先索敌排序 = "顺序";
var 是否追踪 = true;
var 敌人顺序 = 0;
if(_root.控制目标 == _parent._name){
	//if(Key.isDown(_root.武器变形键)){
		//是否强行重新索敌 = true;
	//}
	if(Key.isDown(_root.互动键)){
		优先索敌属性 = "血量";
		优先索敌排序 = "倒序";
	}
	if(Key.isDown(_root.奔跑键)){
		是否追踪 = false;
	}
}
var 追踪目标 = _parent.按距离索敌(索敌距离, 是否强行重新索敌, 优先索敌属性, 优先索敌排序);
if(!追踪目标){
	是否追踪 = false;
}

shootX = _root.gameworld[_parent.追踪目标]._x;
shootY = _root.gameworld[_parent.追踪目标]._y;
shootZ = _root.gameworld[_parent.追踪目标].Z轴坐标;

onClipEvent(load){
	if(!_parent.是否追踪){
		_parent.攻击时按键四向移动(25,100);
	}else{
		_parent.X轴追踪移动(130);
		_parent.Z轴追踪移动();
	}
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{_parent.猎影十字可派生();}
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);
	自机 = _parent._parent;
	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  自机.空手攻击力 / 5 + 自机.刀属性.power * 3;
	if (自机.mp攻击加成 && 自机.等级 >= 10)
	{子弹属性.子弹威力 += 自机.mp攻击加成 * 1;}
	if(自机.被动技能.刀剑攻击 && 自机.被动技能.刀剑攻击.等级 && 自机.等级 >= 15)
	{
		子弹属性.子弹威力 *= 1 + 自机.被动技能.刀剑攻击.等级 / 13;
	}
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 1;
	//子弹属性.不硬直 = true;
	子弹属性.水平击退速度 = 12;
	子弹属性.击中后子弹的效果 = "斩打命中特效";

	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

