onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}

onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}

onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}
