Actionscript.script:
function 消弹()
{
	var 消弹属性 = _root.消弹属性初始化(this.攻击点);
	//var 消弹属性 = _root.消弹属性初始化(this.area);
	消弹属性.消弹方向 = _parent.方向 == "右" ? "左" : "右";
	消弹属性.Z轴攻击范围 = 30;
	消弹属性.区域定位area = this.攻击点;
	//消弹属性.区域定位area = this.area;

	_root.消除子弹(消弹属性);
}
_parent.UpdateSmallState("粉碎切割开始");

// 粉碎切割入口：初始化计数或检查上限
if (typeof 粉碎切割次数 == "undefined")
{
    粉碎切割次数 = 1;  // ← 首次进入时初始化为1
}
else if (粉碎切割次数 >= 2)
{
    // 派生已达2次上限，不能继续派生
    _root.兵器攻击路由.动画完毕(this,_parent);  // ← 直接结束动画
}
else
{
    // 第2、3次派生，自增计数
    粉碎切割次数 += 1;  // ← 继续派生
}

自机 = _parent;
if(自机.动作C)
{
	gotoAndPlay("开始切割");
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{_parent.粉碎切割可派生();}
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);

	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  _parent._parent.空手攻击力 / 5 + _parent._parent.刀属性.power * 1;

	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 20;
	子弹属性.不硬直 = true;

	//子弹属性.水平击退速度 = 12;
	子弹属性.击中后子弹的效果 = "斩打命中特效";

	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);

	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  _parent._parent.空手攻击力 / 5 + _parent._parent.刀属性.power * 1;

	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 20;
	子弹属性.不硬直 = true;

	//子弹属性.水平击退速度 = 12;
	子弹属性.击中后子弹的效果 = "斩打命中特效";

	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

