Actionscript.script:
_parent.UpdateBigSmallState("技能中","凶斩中");

if(_parent.hp >= _parent.hp满血值)
{
	hp溢出 = _parent.hp - _parent.hp满血值;
	//hp溢出 = _parent.hp * 0.5;//
	_parent.hp -= hp溢出;
	_root.主角hp显示界面.刷新显示();
}
else
{
	hp溢出 = 0;
}
轻型武器攻击搓招 = _root.技能函数.轻型武器攻击搓招;
大型武器攻击搓招 = _root.技能函数.大型武器攻击搓招;

_parent.联机2015发送角色数据();
if(_parent.技能浮空 == true)
{
   gotoAndPlay("红凶斩跳前摇");
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);

	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";

	自机 = _root.获得父节点(this,2);
	hp溢出加成 = _parent.hp溢出 * 3;
	子弹属性.子弹威力 = 自机.空手攻击力 * 0.1 + 1000 + 自机.刀属性.power * 3 + hp溢出加成;

	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 10;

	子弹属性.区域定位area = this.area;
	_root.子弹区域shoot传递(子弹属性);
}
	/*
	子弹属性.吸血 = 50;
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 10;
	*/

	//非常用
	/*
	子弹属性.水平击退速度 = NaN;
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.击中后子弹的效果 = "".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/

	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
