onClipEvent (load) {
	if (_parent._parent.刀 != "")
	{
		_parent._parent.根据模式重新读取武器加成("兵器");
	}
	this.onUnload = function()
	{
		if (_parent._parent.状态 == "技能")
		{
			_parent._parent.根据模式重新读取武器加成("空手");
		}
		else if (_parent._parent.攻击模式 != "")
		{
			_parent._parent.根据模式重新读取武器加成(_parent._parent.攻击模式);
		}
	};
}

onClipEvent(enterFrame){
   _parent.攻击时可改变移动方向(25);
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.大型武器攻击搓招();
	}
}

onClipEvent (load) {
	_parent._parent.skillShadow = this;
	if (!_root.是否阴影)
	{
		this._visible = false;
	}
	缩放乘数 = 3.611 * 175 / _parent._parent.身高;
	if (_parent._parent.技能浮空 == true)
	{
		if (_parent._parent.浮空)
		{
			this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
		}
	}
}
onClipEvent (enterFrame) {
	if (_root.是否阴影 and (_parent._parent.浮空 or (_parent._parent.技能浮空 == true)))
	{
		this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
	}
	else
	{
		this._y = 233.05;
	}
}
