Actionscript.script:
持续帧 = 10;
扭转乾坤恢复 = _root.技能函数.扭转乾坤恢复;
_root.技能函数.扭转乾坤护盾释放(_parent,_parent.技能等级);
许可 = true;

持续帧 += 20;
if(持续帧 < 30 + _parent.技能等级 * 12){
	gotoAndPlay("循环");
}

if (_parent.被动技能.内力爆发 && _parent.被动技能.内力爆发.启用 && _parent.hp > 10 && _parent.mp > 10)
{
	_parent.hp -= 10;
	_parent.mp -= 10;
	var 恢复值 = _parent.内力 * (10 + _parent.被动技能.内力爆发.等级) / 10;
	//_parent.mp += _parent.内力 * ( 10 + _parent.被动技能.内力爆发.等级) / 20;
	var effects = [{type:"regen", hp:恢复值, mp:恢复值, duration:150, interval:30, mode:"total", id:"内力周转_缓释_聚气", scaleWithAlchemy:false}];
	_root.释放药剂效果(_parent,effects)
}

onClipEvent (enterFrame) {
	if (_parent._parent._name == _root.控制目标 && Key.isDown(_parent._parent.技能按键值) == true && _parent.许可)
	{
		_parent.许可 = true;
	}
	else
	{
		_parent.许可 = false;
		_parent.gotoAndPlay("扭转乾坤结束");
	}
}
/*
onClipEvent (load) {
_root.技能函数.扭转乾坤护盾释放(_parent._parent,_parent._parent.技能等级);
_parent.许可 = true;
}
*/
onClipEvent (unload) {
	var 护盾ID = _parent._parent.扭转乾坤护盾ID;
	if (护盾ID != undefined)
	{
		var 护盾对象 = _parent._parent.shield.getShieldById(护盾ID);

		if (护盾对象 != null)
		{
			// 移除前读取剩余容量
			var 剩余容量 = 护盾对象.getCapacity();
			// 计算实际承伤量 = 总容量 - 剩余容量
			if(剩余容量){
				_parent.扭转乾坤护盾承伤量 = _parent.扭转乾坤护盾容量 - 剩余容量;
			}
			_parent.许可 = false;
		}
		// 移除护盾  
		_parent._parent.shield.removeShieldById(护盾ID);
	}
	if(_parent.扭转乾坤护盾承伤量){
		_parent.扭转乾坤恢复(_parent.扭转乾坤护盾承伤量,_parent._parent.技能等级);
	}
}

onClipEvent (load) {
	_parent._parent.skillShadow = this;
	if (!_root.是否阴影)
	{
		this._visible = false;
	}
	缩放乘数 = 3.611 * 175 / _parent._parent.身高;
	if (_parent._parent.技能浮空 == true)
	{
		if (_parent._parent.浮空)
		{
			this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
		}
	}
}
onClipEvent (enterFrame) {
	if (_root.是否阴影 && !_parent.落地)
	{
		this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
	}
	else
	{
		this._y = 233.05;
	}
}
