Actionscript.script:
onClipEvent(enterFrame){
   _parent.攻击时移动(5,5);
}

onClipEvent(load){
   声音 = "";
   霰弹值 = 1;
   子弹散射度 = 0;
   发射效果 = "";
   子弹种类 = "近战子弹";
   var temp = 1;
   if(_root.手部装备 == "褐色道钉手套")
   {
      temp = 5;
   }
   子弹威力 = _parent._parent.空手攻击力 * 1.5 * _parent._parent.技能等级 * temp;
   子弹速度 = 0;
   击中地图效果 = "";
   Z轴攻击范围 = 30;
   击倒率 = 20;
   击中后子弹的效果 = "空手攻击火花";
   if(_parent._parent.是否为敌人 == true)
   {
      子弹敌我属性 = false;
   }
   else
   {
      子弹敌我属性 = true;
   }
   var myPoint = {x:this._x,y:this._y};
   _parent.localToGlobal(myPoint);
   _root.gameworld.globalToLocal(myPoint);
   shootX = myPoint.x;
   shootY = myPoint.y;
   _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,_parent._parent._name,shootX,shootY,_parent._parent.Z轴坐标,子弹敌我属性,击倒率,击中后子弹的效果);
}

