Actionscript.script:
/*if (_root.天气系统.空间情况 === "室内")
{
	_root.发布消息("火力支援无法在室内使用！");
}*/
_root.gameworld.attachMovie("火力支援开始","火力支援开始" + random(999),_root.gameworld.getNextHighestDepth(),{轰炸总次数:10 + Number(_parent.技能等级) * 2});

onClipEvent (load) {
	_parent._parent.skillShadow = this;
	if (!_root.是否阴影)
	{
		this._visible = false;
	}
	缩放乘数 = 3.611 * 175 / _parent._parent.身高;
	if (_parent._parent.技能浮空 == true)
	{
		if (_parent._parent.浮空)
		{
			this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
		}
	}
}
onClipEvent (enterFrame) {
	if (_root.是否阴影 && !_parent.落地)
	{
		this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
	}
	else
	{
		this._y = 233.05;
	}
}
