Actionscript.script:
空手攻击搓招 = _root.技能函数.空手攻击搓招;
诛杀步可派生搓招 = _root.技能函数.诛杀步可派生搓招;
后撤步可派生搓招 = _root.技能函数.后撤步可派生搓招;
波动拳可派生搓招 = _root.技能函数.波动拳可派生搓招;
能量喷泉可派生搓招 = _root.技能函数.能量喷泉可派生搓招;
燃烧指节可派生搓招 = _root.技能函数.燃烧指节可派生搓招;

var 狼炮耗蓝 = Math.ceil(_parent.mp满血值 * 0.1);

if(_parent.mp >= 狼炮耗蓝 && _parent.mp满血值 >= 800)
{
	_parent.mp -= 狼炮耗蓝;
}
else
{_root.空手攻击路由.动画完毕(this,_parent);}

if(_parent.性别 == "女")
{
	//_parent.攻击呐喊();
	_root.播放音效("女声_Power Wave.wav");
}
else
{
	//_parent.攻击呐喊();
	_root.播放音效("男声_Power Wave.wav");
}

onClipEvent(load){
	子弹属性 = _root.子弹属性初始化(this);
	var 自机 = _parent._parent;
	//以下部分只需要更改需要更改的属性,其余部分可注释掉
	子弹属性.声音 = "";
	子弹属性.霰弹值 = 1;
	子弹属性.子弹散射度 = 0;
	子弹属性.子弹种类 = "近战子弹";
	子弹属性.子弹威力 =  自机.空手攻击力 * 1 + 自机.内力 * 3;
	if (_parent._parent.被动技能.拳脚攻击 && _parent._parent.被动技能.拳脚攻击.启用)
	{
		子弹属性.子弹威力 += _parent._parent.空手攻击力 * _parent._parent.被动技能.拳脚攻击.等级 * 0.1;
	}
	if (_parent._parent.mp攻击加成)
	{
		子弹属性.子弹威力 += _parent._parent.mp攻击加成;
	}
	if (_parent._parent.被动技能.升龙拳 && _parent._parent.被动技能.升龙拳.等级)
	{
		子弹属性.子弹威力 += _parent._parent.空手攻击力 * _parent._parent.被动技能.升龙拳.等级 / 10;
	}
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 1;
	子弹属性.不硬直 = true;
	子弹属性.水平击退速度 = 15;
	子弹属性.击中后子弹的效果 = "空中小火焰爆炸";
	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

