	if (_parent._parent.mp攻击加成)
	{
		子弹属性.子弹威力 += _parent._parent.mp攻击加成;
	}
	if (_parent._parent.被动技能.升龙拳 && _parent._parent.被动技能.升龙拳.等级)
	{
		子弹属性.子弹威力 += _parent._parent.空手攻击力 * _parent._parent.被动技能.升龙拳.等级 / 10;
	}
	子弹属性.子弹速度 = 0;
	子弹属性.Z轴攻击范围 = 30;
	子弹属性.击倒率 = 1;
	子弹属性.水平击退速度 = 15;
	子弹属性.击中后子弹的效果 = "";
	//非常用
	/*
	子弹属性.垂直击退速度 = NaN;
	子弹属性.发射效果 = "";
	子弹属性.击中地图效果 = "火花".
	子弹属性.命中率 = NaN.
	子弹属性.固伤 = NaN;
	子弹属性.百分比伤害 = NaN;
	子弹属性.血量上限击溃 = NaN;
	子弹属性.防御粉碎 = NaN;
	*/
	子弹属性.区域定位area = this.area;
	/*
	//已初始化内容，通常不需要重复赋值，可注释掉
	子弹属性.发射者 = 子弹属性.发射者.
	子弹属性.shootX = 子弹属性.shootX;
	子弹属性.shootY = 子弹属性.shootY;
	子弹属性.shootZ = 子弹属性.shootZ;
	子弹属性.子弹敌我属性 = 子弹属性.子弹敌我属性;
	*/
	_root.子弹区域shoot传递(子弹属性);
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.燃烧指节可派生搓招();

		if (自机.动作A && 自机.动作B)//J+K
		{
			if(自机.被动技能.内力爆发 && 自机.被动技能.内力爆发.启用){
				_parent.gotoAndPlay("狼炮");
			}
		}
	}
}

onClipEvent (enterFrame) {
	var 自机 = _parent._parent;
	if (自机._name == _root.控制目标)
	{
		_parent.狼炮可派生搓招();
	}
}

onClipEvent(load){
   if(_root.是否阴影 == true)
   {
      this._visible = 1;
   }
   else
   {
      this._visible = 0;
   }
}
