onClipEvent (load) {
	if (_parent.径庭拳许可)
	{
		子弹属性 = _root.子弹属性初始化(this);

		//以下部分只需要更改需要更改的属性,其余部分可注释掉
		子弹属性.声音 = "";
		子弹属性.霰弹值 = 6;
		子弹属性.子弹散射度 = 0;
		子弹属性.子弹种类 = "近战联弹";
		子弹属性.子弹威力 = 1.2 * (_parent._parent.空手攻击力  + _parent._parent.内力) * (3 + _parent._parent.技能等级 * 1);
		if (_parent._parent.mp攻击加成)
		{
			子弹属性.子弹威力 += 1.2 * _parent._parent.mp攻击加成;
		}
		子弹属性.子弹速度 = 0;
		子弹属性.Z轴攻击范围 = 30;
		子弹属性.击倒率 = 10;

		//非常用
		/*
		子弹属性.水平击退速度 = NaN;
		子弹属性.垂直击退速度 = NaN;
		子弹属性.发射效果 = "";
		子弹属性.击中地图效果 = "火花".
		子弹属性.击中后子弹的效果 = "".
		子弹属性.命中率 = NaN.
		子弹属性.固伤 = NaN;
		子弹属性.百分比伤害 = NaN;
		子弹属性.血量上限击溃 = NaN;
