		_root.子弹区域shoot传递(子弹属性);
	}
}

onClipEvent (load) {
	if (Key.isDown(_root.奔跑键) == true)
	{
		_parent.攻击时移动(50,125);
	}
	else
	{
		_parent.攻击时移动(25,50);
	}
}

