onClipEvent (enterFrame) {
	if (_parent._parent._name == _root.控制目标 && Key.isDown(_parent._parent.技能按键值) == true)
	{
		if (未按技能键)
		{
			_parent.径庭拳许可 = true;
		}
	}
	else
	{
		未按技能键 = true;
	}
}
onClipEvent (load) {
	if (_parent._parent._name == _root.控制目标 && Key.isDown(_parent._parent.技能按键值) == true)
	{
		未按技能键 = false;
	}
	else
	{
		未按技能键 = true;
	}
}

onClipEvent (load) {
	_parent._parent.skillShadow = this;
	if (!_root.是否阴影)
	{
		this._visible = false;
	}
	缩放乘数 = 3.611 * 175 / _parent._parent.身高;
	if (_root.技能浮空 == true && _parent._parent._name == _root.控制目标)
	{
		if (_parent._parent.浮空)
		{
			this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
		}
	}
}
onClipEvent (enterFrame) {
	if (_root.是否阴影 && !_parent.落地)
	{
		this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
	}
	else
	{
		this._y = 233.05;
	}
}
