Actionscript.script:
onClipEvent(load){
   声音 = "";
   霰弹值 = 1;
   子弹散射度 = 0;
   发射效果 = "";
   子弹种类 = "近战子弹";
   子弹威力 = _parent._parent.空手攻击力 * 4 * _parent._parent.技能等级;
   子弹速度 = 0;
   击中地图效果 = "";
   Z轴攻击范围 = 30;
   击倒率 = 0;
   击中后子弹的效果 = "空手攻击火花";
   if(_parent._parent.是否为敌人 == true)
   {
      子弹敌我属性 = false;
   }
   else
   {
      子弹敌我属性 = true;
   }
   var myPoint = {x:this._x,y:this._y};
   _parent.localToGlobal(myPoint);
   _root.gameworld.globalToLocal(myPoint);
   shootX = myPoint.x;
   shootY = myPoint.y;
   _root.子弹区域shoot(声音,霰弹值,子弹散射度,发射效果,子弹种类,子弹威力,子弹速度,Z轴攻击范围,击中地图效果,_parent._parent._name,shootX,shootY,_parent._parent.Z轴坐标,子弹敌我属性,击倒率,击中后子弹的效果);
}

onClipEvent(unload){
   if(_parent.投技目标 != "" and _parent.投技目标 != undefined)
   {
      _root.gameworld[_parent.投技目标].状态改变("击倒");
      _parent.投技目标 = "";
   }
}

onClipEvent(load){
   _parent.投技目标 = "";
   for(each in _root.gameworld)
   {
      if(_root.gameworld[each].hitTest(this) and _root.gameworld[each].是否为敌人 == true and _root.gameworld[each].hp > 0 and Math.abs(_parent._parent.Z轴坐标 - _root.gameworld[each].Z轴坐标) < 50)
      {
         _parent.投技目标 = _root.gameworld[each]._name;
         break;
      }
   }
   if(_parent.投技目标 == "")
   {
      //_parent._parent.动画完毕();
	  _parent._parent.技能名 = "寸拳"
	  _parent.gotoAndPlay(2);//直接跳转到寸拳有问题，所以跳到第2帧
   }
}

onClipEvent(enterFrame){
   if(_parent.投技目标 != "" and _parent.投技目标 != undefined)
   //检查 _parent 对象的 投技目标 属性是否不为空且不为 undefined。这表明目标存在
   {
      _root.gameworld[_parent.投技目标].状态改变("被投");
	  //调用目标对象的 状态改变 方法，设置状态为 "被投"。这是用来标记目标已被投技击中

      var myPoint = {x:this._x,y:this._y};
	  //创建一个包含当前对象位置的点 myPoint，this._x 和 this._y 分别是当前对象的 X 和 Y 坐标
      _parent.localToGlobal(myPoint);
	  //将 myPoint 从局部坐标系转换为全局坐标系。
      _root.gameworld.globalToLocal(myPoint);
	  //将 myPoint 从全局坐标系转换回游戏世界的局部坐标系
      shootX = myPoint.x;
      shootY = myPoint.y;
	  //将计算得到的坐标分配给 shootX 和 shootY

      if(shootX < _root.Xmax and shootX > _root.Xmin and shootY < _root.Ymax and shootY > _root.Ymin and _root.gameworld.地图.hitTest(shootX,shootY,true) == false)
	  //检查 shootX 和 shootY 是否在允许的范围内，并且确保这些坐标点不与地图的碰撞区域重叠
      {
         _root.gameworld[_parent.投技目标]._x = shootX;
         _root.gameworld[_parent.投技目标]._y = shootY;
		 //如果上述条件成立，更新投技目标的位置为 shootX 和 shootY
      }

      _root.gameworld[_parent.投技目标].man._rotation = this._rotation;
	  //将当前对象的旋转角度设置为投技目标的旋转角度。这可能是为了确保目标的方向与投技的方向一致

      if(_parent._parent.方向 == "右" and this._xscale > 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "右";
		 //如果父对象的方向是 "右" 并且当前对象的 X 轴缩放比例 (_xscale) 大于 0，则设置投技目标的方向为 "右"
      }
      else if(_parent._parent.方向 == "右" and this._xscale < 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "左";
		 //如果父对象的方向是 "右" 但当前对象的 X 轴缩放比例小于 0，则设置投技目标的方向为 "左"
      }
      else if(_parent._parent.方向 == "左" and this._xscale > 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "左";
		 //如果父对象的方向是 "左" 并且当前对象的 X 轴缩放比例大于 0，则设置投技目标的方向为 "左"
      }
      else if(_parent._parent.方向 == "左" and this._xscale < 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "右";
		 //如果父对象的方向是 "左" 但当前对象的 X 轴缩放比例小于 0，则设置投技目标的方向为 "右"
      }
      _root.gameworld[_parent.投技目标].方向改变(_root.gameworld[_parent.投技目标].方向);
	  //调用投技目标的 方向改变 方法，传递当前设置的方向。这是用来更新目标的方向状态。
   }
}

onClipEvent(enterFrame){
   if(_parent.投技目标 != "" and _parent.投技目标 != undefined)
   {
      _root.gameworld[_parent.投技目标].状态改变("被投");
      var myPoint = {x:this._x,y:this._y};
      _parent.localToGlobal(myPoint);
      _root.gameworld.globalToLocal(myPoint);
      shootX = myPoint.x;
      shootY = myPoint.y;
      if(shootX < _root.Xmax and shootX > _root.Xmin and shootY < _root.Ymax and shootY > _root.Ymin and _root.gameworld.地图.hitTest(shootX,shootY,true) == false)
      {
         _root.gameworld[_parent.投技目标]._x = shootX;
         _root.gameworld[_parent.投技目标]._y = shootY;
      }
      _root.gameworld[_parent.投技目标].man._rotation = this._rotation;
      if(_parent._parent.方向 == "右" and this._xscale > 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "右";
      }
      else if(_parent._parent.方向 == "右" and this._xscale < 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "左";
      }
      else if(_parent._parent.方向 == "左" and this._xscale > 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "左";
      }
      else if(_parent._parent.方向 == "左" and this._xscale < 0)
      {
         _root.gameworld[_parent.投技目标].方向 = "右";
      }
      _root.gameworld[_parent.投技目标].方向改变(_root.gameworld[_parent.投技目标].方向);
   }
}

