Actionscript.script:
if (_parent.已使用铁布衫 != true)
{
	var 技能防御力加成 = 0.99 + 0.08 * _parent.技能等级 + Math.min(_parent.内力/7000, 0.1);
	_parent.buff.赋值("防御力","倍率",技能防御力加成,"增益");
	//_parent.防御力 += _parent.技能防御力加成;
	var 加成比例 =  -1 + 8 * _parent.技能等级 + Math.floor(Math.min(_parent.内力/70, 10));
	_root.发布消息("防御力上升" + 加成比例 + "%！目前防御力为" + Math.floor(_parent.防御力) + "点！");
}
_parent.已使用铁布衫 = true;

onClipEvent (load) {
	_parent._parent.skillShadow = this;
	if (!_root.是否阴影)
	{
		this._visible = false;
	}
	缩放乘数 = 3.611 * 175 / _parent._parent.身高;
	if (_parent._parent.技能浮空 == true)
	{
		if (_parent._parent.浮空)
		{
			this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
		}
	}
}
onClipEvent (enterFrame) {
	if (_root.是否阴影 && !_parent.落地)
	{
		this._y = 233.05 + (_parent._parent.起始Y - _parent._parent._y - _parent._parent.垂直速度) * 缩放乘数;
	}
	else
	{
		this._y = 233.05;
	}
}
