Actionscript.script:
function 攻击时移动(慢速度, 快速度)
{
   if(_parent.方向 == "右")
   {
      if(Key.isDown(_root.右键) == true)
      {
         _parent.移动("右",快速度);
      }
      else
      {
         _parent.移动("右",慢速度);
      }
   }
   else if(_parent.方向 == "左")
   {
      if(Key.isDown(_root.左键) == true)
      {
         _parent.移动("左",快速度);
      }
      else
      {
         _parent.移动("左",慢速度);
      }
   }
}

if(_parent.浮空高度>30 && !_parent.锁定高度){
	已降低高度 += 30;
	_parent.浮空高度 -= 30;
}

if(_parent.浮空高度>30 && !_parent.锁定高度){
	已降低高度 += 20;
	_parent.浮空高度 -= 20;
}

if(_parent.浮空高度>30 && !_parent.锁定高度){
	已降低高度 += 20;
	_parent.浮空高度 -= 20;
}

if(已降低高度>40 && !_parent.锁定高度){
	已降低高度 -= 20;
	_parent.浮空高度 += 20;
}

if(已降低高度>40 && !_parent.锁定高度){
	已降低高度 -= 20;
	_parent.浮空高度 += 20;
}

if(已降低高度>40 && !_parent.锁定高度){
	已降低高度 -= 40;
	_parent.浮空高度 += 40;
}

onClipEvent (load) {
    // 初始化子弹属性
    子弹属性 = _root.子弹属性初始化(this);

    // 设置基本属性
    子弹属性.声音 = "";
    子弹属性.霰弹值 = 1;
    子弹属性.子弹散射度 = 0;
    子弹属性.发射效果 = "";
    子弹属性.子弹种类 = "近战子弹";
    子弹属性.子弹威力 = _parent._parent.空手攻击力;
    子弹属性.子弹速度 = 0;
    子弹属性.击中地图效果 = "";
    子弹属性.Z轴攻击范围 = 30;
    子弹属性.击倒率 = 10;
    子弹属性.击中后子弹的效果 = "空手攻击火花";

    // 击退速度设置为NaN（原代码中没有击退速度参数）
    子弹属性.水平击退速度 = NaN;
    子弹属性.垂直击退速度 = NaN;
    子弹属性.伤害类型 = "破击";
    子弹属性.魔法伤害属性 = "原体";

    // 坐标转换
    var myPoint = {x:this._x, y:this._y};
    _parent.localToGlobal(myPoint);
    _root.gameworld.globalToLocal(myPoint);

    // 设置发射坐标和发射者信息
    子弹属性.shootX = myPoint.x;
    子弹属性.shootY = myPoint.y;
    子弹属性.shootZ = _parent._parent.Z轴坐标;
    子弹属性.发射者 = _parent._parent._name;

    // 调用新的子弹发射函数
    _root.子弹区域shoot传递(子弹属性);
}

