初始化敌人模板 = _root.初始化敌人模板;
初始化敌人模板();

if(this.已变身最后阶段){
	this.变身控制块.temp_MovieFire.removeMovieClip();
}else{
	if(this.变身阶段 == 2){
		this.已变身最后阶段 = true;
	}
	gotoAndStop("倒地");
}

onClipEvent(enterFrame){
    if(_parent.已使用兴奋剂 != true)
   {
      if( _parent.宠物属性.涅槃重生 || (_parent.hp <= 1500 and _parent.hp > 0 and _parent.变身阶段 == 0))
      {
         //_parent.无敌 = true;
         _root.效果("浴火重生",_parent._x,_parent._y,100);
         _root.效果("火回旋",_parent._x,_parent._y,100);
         _root.效果("火攻",_parent._x,_parent._y,100);
         _parent.hp = _parent.hp满血值;
         _parent.空手攻击力 *= 0.7;
         _parent.行走X速度 *= 1.5;
         _parent.行走Y速度 *= 1.5;
         _parent.跑X速度 *= 1.5;
         _parent.跑Y速度 *= 1.5;
         _root.发布消息("燎原烈火……");
         _parent.已使用兴奋剂 = true;
         _parent.变身阶段 = 1;
      }
   }
   else if(_parent.hp <= 1500 and _parent.hp > 0 and _parent.变身阶段 == 1)
   {
      //_parent.无敌 = true;
      _root.效果("熔炎裂渊",_parent._x,_parent._y,100);
      _root.效果("火回旋",_parent._x,_parent._y,100);
      _root.效果("火攻",_parent._x,_parent._y,100);
      _parent.hp满血值 = 1500;
      _parent.hp = _parent.hp满血值;
      _root.发布消息("涅槃重生……");
      _parent.已使用兴奋剂 = true;
      _parent.变身阶段 = 2;
      temp_MovieFire = _parent.attachMovie("火凤余烬","火凤余烬",_parent.getNextHighestDepth(),{_xscale:100,_yscale:150});
      temp_MovieFire.play();
      temp_MovieFire.onEnterFrame = function()
      {
         if(this._currentFrame == this._totalFrames - 1)
         {
            this.gotoAndPlay(1);
         }
         if(_parent._parent.hp <= 0 and _parent._parent.变身阶段 == 2)
         {
            this.gotoAndPlay(this._totalFrames);
            this.play();
            delete this.onEnterFrame;
         }
      };
   }
}

onClipEvent(enterFrame){
   if(this._currentframe == 1)
   {
      if(this.hitTest(_root.gameworld[_parent.攻击目标].area) and Math.abs(_parent._y - _root.gameworld[_parent.攻击目标].Z轴坐标) <= _parent.y轴攻击范围)
      {
         play();
         if(random(_parent.攻击欲望) == 0)
         {
            _parent.近战招式 = "a1";
            _parent.状态改变("近战");
         }
      }
   }
}

