onClipEvent (load) {
	if (_parent.hp > 0 && _parent.宠物属性.涅槃重生)
	{
		_parent.状态改变("被击");
	}
	else
	{
		_parent.击飞浮空();
	}
}

onClipEvent (enterFrame) {
	if (_parent.已使用兴奋剂 != true and _parent.hp < _parent.hp满血值 and _parent.hp > 0 and _root.暂停 == false)
	{
		_parent.hp += 17;
	}
	else if (_parent.已使用兴奋剂 == true and _parent.hp > 0 and _root.暂停 == false)
	{
		if (_parent.变身阶段 == 1)
		{
			if (_parent.宠物属性.涅槃重生)
			{
				if (_parent.hp < _parent.hp满血值)
				{
					_parent.hp += 17;
				}
			}
			else
			{
				_parent.hp -= _parent.hp满血值 / 3600;
			}
		}
		else if (_parent.变身阶段 == 2 and _parent.hp < _parent.hp满血值)
		{
			_parent.hp += 105;
		}
	}
}

