Actionscript.script:
// 导入必要的库
import flash.filters.ColorMatrixFilter;
import flash.geom.ColorTransform;


// 常量定义
var TOTAL_CONTAINERS:Number = 10; // 总共的容器数量

// 自定义 toFixed 函数：格式化数字到指定小数位数
_root.toFixed = function(num:Number, precision:Number):String {
    // 默认精度为1位
    if (precision == undefined) {
        precision = 1;
    }

    // 计算10的精度次方
    var multiplier:Number = Math.pow(10, precision);

    // 四舍五入到指定小数位数
    var roundedValue:Number = Math.round(num * multiplier) / multiplier;

    // 将数值转换为字符串
    var valueString:String = roundedValue.toString();

    // 确保结果包含指定的小数位数
    if (precision > 0) {
        var decimalIndex:Number = valueString.indexOf(".");
        if (decimalIndex == -1) {
            // 没有小数点，补充小数部分
            valueString += ".";
            for (var i:Number = 0; i < precision; i++) {
                valueString += "0";
            }
        } else {
            // 有小数点，补充不足的小数位
            var decimals:Number = valueString.length - decimalIndex - 1;
            while (decimals < precision) {
                valueString += "0";
                decimals++;
            }
        }
    }

    return valueString;
};

// 正确实现的 ColorMatrix 分解函数
_root.decomposeColorMatrix = function(matrix:Array):Object {
    var params:Object = {
        brightness: 0,
        contrast: 0,
        saturation: 0,
        hue: 0
    };

    // Luminance coefficients for RGB
    var lumaR:Number = 0.2126;
    var lumaG:Number = 0.7152;
    var lumaB:Number = 0.0722;

    // Extract scale factors from the diagonal elements
    var scaleR:Number = matrix[0];     // m00
    var scaleG:Number = matrix[6];     // m11
    var scaleB:Number = matrix[12];    // m22

    // Estimate contrast (average scale factor)
    var avgScale:Number = (scaleR + scaleG + scaleB) / 3;
    params.contrast = (avgScale - 1) * 100; // Convert to percentage

    // Extract offsets from the last column
    var offsetR:Number = matrix[4];     // m04
    var offsetG:Number = matrix[9];     // m14
    var offsetB:Number = matrix[14];    // m24

    // Estimate brightness
    // Adjust for contrast offset: offset = scale * brightness + 128 * (1 - scale)
    var avgOffset:Number = (offsetR + offsetG + offsetB) / 3;
    var brightnessValue:Number = (avgOffset - 128 * (1 - avgScale)) / avgScale;
    params.brightness = (brightnessValue / 255) * 100; // Convert to percentage

    // Estimate saturation
    // Based on the difference between the adjusted scale factors and luminance coefficients
    var satR:Number = (scaleR - lumaR * (1 - avgScale)) / avgScale;
    var satG:Number = (scaleG - lumaG * (1 - avgScale)) / avgScale;
    var satB:Number = (scaleB - lumaB * (1 - avgScale)) / avgScale;
    var saturationAvg:Number = ((satR + satG + satB) / 3 - 1) * 100;
    params.saturation = saturationAvg;

    // Estimate hue (complex due to trigonometric functions)
    // Use off-diagonal elements to approximate hue angle
    var hueNumerator:Number = matrix[1] - matrix[5];  // m01 - m10
    var hueDenominator:Number = matrix[0] + matrix[6] - 2 * avgScale;  // m00 + m11 - 2 * avgScale
    var hueAngle:Number = Math.atan2(hueNumerator, hueDenominator);
    params.hue = hueAngle * (180 / Math.PI); // Convert to degrees

    // Adjust hue value to be within -180 to 180 degrees
    if (params.hue > 180) params.hue -= 360;
    if (params.hue < -180) params.hue += 360;

    return params;
};


// 提取 ColorMatrixFilter 参数
_root.extractColorMatrixParams = function(target:MovieClip):Object {
    var params:Object = {
        brightness: 0,
        contrast: 0,
        saturation: 0,
        hue: 0
    };

    if (!target) {
        trace("Error: Target MovieClip is undefined.");
        return params;
    }

    var filters:Array = target.filters;
    for (var i:Number = 0; i < filters.length; i++) {
        if (filters[i] instanceof ColorMatrixFilter) {
            var matrix:Array = filters[i].matrix;
            var decomposedParams:Object = _root.decomposeColorMatrix(matrix);
            params.brightness = decomposedParams.brightness;
            params.contrast = decomposedParams.contrast;
            params.saturation = decomposedParams.saturation;
            params.hue = decomposedParams.hue;
        }
    }

    return params;
};

// 提取 ColorTransform 参数
_root.extractColorTransformParams = function(target:MovieClip):Object {
    var params:Object = {
        redMultiplier: 1,
        greenMultiplier: 1,
        blueMultiplier: 1,
        alphaMultiplier: 1,
        redOffset: 0,
        greenOffset: 0,
        blueOffset: 0,
        alphaOffset: 0
    };

    if (!target) {
        trace("Error: Target MovieClip is undefined.");
        return params;
    }

    var colorTransform:ColorTransform = target.transform.colorTransform;
    params.redMultiplier = colorTransform.redMultiplier;
    params.greenMultiplier = colorTransform.greenMultiplier;
    params.blueMultiplier = colorTransform.blueMultiplier;
    params.alphaMultiplier = colorTransform.alphaMultiplier;
    params.redOffset = colorTransform.redOffset;
    params.greenOffset = colorTransform.greenOffset;
    params.blueOffset = colorTransform.blueOffset;
    params.alphaOffset = colorTransform.alphaOffset;

    return params;
};

// 格式化参数为 XML，使用 toFixed 函数限制到1位小数
_root.formatParamsToXML = function(level:Number, params:Object):String {
    var xml:String = "<Preset>\n";
    xml += "  <level>" + _root.toFixed(level, 0) + "</level>\n"; // level 为整数，不需要小数
    xml += "  <Brightness>" + _root.toFixed(params.brightness, 1) + "</Brightness>\n";
    xml += "  <Contrast>" + _root.toFixed(params.contrast, 1) + "</Contrast>\n";
    xml += "  <Saturation>" + _root.toFixed(params.saturation, 1) + "</Saturation>\n";
    xml += "  <Hue>" + _root.toFixed(params.hue, 1) + "</Hue>\n";
    xml += "  <redMultiplier>" + _root.toFixed(params.redMultiplier, 1) + "</redMultiplier>\n";
    xml += "  <greenMultiplier>" + _root.toFixed(params.greenMultiplier, 1) + "</greenMultiplier>\n";
    xml += "  <blueMultiplier>" + _root.toFixed(params.blueMultiplier, 1) + "</blueMultiplier>\n";
    xml += "  <alphaMultiplier>" + _root.toFixed(params.alphaMultiplier, 1) + "</alphaMultiplier>\n";
    xml += "  <redOffset>" + _root.toFixed(params.redOffset, 1) + "</redOffset>\n";
    xml += "  <greenOffset>" + _root.toFixed(params.greenOffset, 1) + "</greenOffset>\n";
    xml += "  <blueOffset>" + _root.toFixed(params.blueOffset, 1) + "</blueOffset>\n";
    xml += "  <alphaOffset>" + _root.toFixed(params.alphaOffset, 1) + "</alphaOffset>\n";
    xml += "</Preset>";
    return xml;
};

// 计算插值参数
_root.interpolateParams = function(startParams:Object, endParams:Object, ratio:Number):Object {
    var interpolatedParams:Object = {};
    for (var key in startParams) {
        interpolatedParams[key] = startParams[key] + (endParams[key] - startParams[key]) * ratio;
    }
    return interpolatedParams;
};

// 生成单个 MovieClip 的预设 XML
_root.generatePresetXML = function(target:MovieClip, level:Number):String {
    if (!target) {
        trace("Error: Target MovieClip is undefined.");
        return "";
    }

    var params:Object = {};

    // 特殊处理容器7
    if (level == 7) {
        // 移除所有滤镜和颜色变换
        target.filters = [];
        target.transform.colorTransform = new ColorTransform();
        // 参数视为0
        params = {
            brightness: 0,
            contrast: 0,
            saturation: 0,
            hue: 0,
            redMultiplier: 1,
            greenMultiplier: 1,
            blueMultiplier: 1,
            alphaMultiplier: 1,
            redOffset: 0,
            greenOffset: 0,
            blueOffset: 0,
            alphaOffset: 0
        };
    } else {
        // 检查是否有滤镜或颜色变换
        var hasColorMatrix:Boolean = false;
        var hasColorTransform:Boolean = false;

        var filters:Array = target.filters;
        for (var i:Number = 0; i < filters.length; i++) {
            if (filters[i] instanceof ColorMatrixFilter) {
                hasColorMatrix = true;
                break;
            }
        }

        var ct:ColorTransform = target.transform.colorTransform;
        if (ct.redMultiplier != 1 || ct.greenMultiplier != 1 || ct.blueMultiplier != 1 || ct.alphaMultiplier != 1 ||
            ct.redOffset != 0 || ct.greenOffset != 0 || ct.blueOffset != 0 || ct.alphaOffset != 0) {
            hasColorTransform = true;
        }

        if (hasColorMatrix || hasColorTransform) {
            // 提取参数
            var colorMatrixParams:Object = _root.extractColorMatrixParams(target);
            var colorTransformParams:Object = _root.extractColorTransformParams(target);

            // 合并参数
            params = {};
            for (var key1 in colorMatrixParams) {
                params[key1] = colorMatrixParams[key1];
            }
            for (var key2 in colorTransformParams) {
                params[key2] = colorTransformParams[key2];
            }
        } else {
            // 未配置滤镜或颜色变换，需要进行插值计算
            params = _root.calculateInterpolatedParams(level);
            // 应用计算得到的滤镜和颜色变换
            _root.applyParamsToTarget(target, params);
        }
    }

    // 格式化为 XML
    var xml:String = _root.formatParamsToXML(level, params);
    return xml;
};

// 计算插值参数的函数
_root.calculateInterpolatedParams = function(level:Number):Object {
    var prevLevel:Number = -1;
    var nextLevel:Number = -1;
    var prevParams:Object = null;
    var nextParams:Object = null;

    // 向前寻找有配置的容器
    for (var i:Number = level - 1; i >= 0; i--) {
        var containerName:String = "容器" + i;
        var container:MovieClip = _root[containerName];
        if (_root.isContainerConfigured(container, i)) {
            prevLevel = i;
            prevParams = _root.getContainerParams(container, i);
            break;
        }
    }

    // 向后寻找有配置的容器
    for (var j:Number = level + 1; j < TOTAL_CONTAINERS; j++) {
        var containerNameNext:String = "容器" + j;
        var containerNext:MovieClip = _root[containerNameNext];
        if (_root.isContainerConfigured(containerNext, j)) {
            nextLevel = j;
            nextParams = _root.getContainerParams(containerNext, j);
            break;
        }
    }

    // 如果前后都没有配置，则参数为0
    if (prevParams == null && nextParams == null) {
        return {
            brightness: 0,
            contrast: 0,
            saturation: 0,
            hue: 0,
            redMultiplier: 1,
            greenMultiplier: 1,
            blueMultiplier: 1,
            alphaMultiplier: 1,
            redOffset: 0,
            greenOffset: 0,
            blueOffset: 0,
            alphaOffset: 0
        };
    }

    // 如果只有前面有配置
    if (prevParams != null && nextParams == null) {
        return prevParams;
    }

    // 如果只有后面有配置
    if (prevParams == null && nextParams != null) {
        return nextParams;
    }

    // 进行插值计算
    var ratio:Number = (level - prevLevel) / (nextLevel - prevLevel);
    var interpolatedParams:Object = _root.interpolateParams(prevParams, nextParams, ratio);

    return interpolatedParams;
};

// 判断容器是否有配置
_root.isContainerConfigured = function(target:MovieClip, level:Number):Boolean {
    if (level == 7) {
        return false; // 容器7不算配置
    }

    var hasColorMatrix:Boolean = false;
    var hasColorTransform:Boolean = false;

    var filters:Array = target.filters;
    for (var i:Number = 0; i < filters.length; i++) {
        if (filters[i] instanceof ColorMatrixFilter) {
            hasColorMatrix = true;
            break;
        }
    }

    var ct:ColorTransform = target.transform.colorTransform;
    if (ct.redMultiplier != 1 || ct.greenMultiplier != 1 || ct.blueMultiplier != 1 || ct.alphaMultiplier != 1 ||
        ct.redOffset != 0 || ct.greenOffset != 0 || ct.blueOffset != 0 || ct.alphaOffset != 0) {
        hasColorTransform = true;
    }

    return hasColorMatrix || hasColorTransform;
};

// 获取容器的参数
_root.getContainerParams = function(target:MovieClip, level:Number):Object {
    var colorMatrixParams:Object = _root.extractColorMatrixParams(target);
    var colorTransformParams:Object = _root.extractColorTransformParams(target);

    // 合并参数
    var params:Object = {};
    for (var key1 in colorMatrixParams) {
        params[key1] = colorMatrixParams[key1];
    }
    for (var key2 in colorTransformParams) {
        params[key2] = colorTransformParams[key2];
    }

    return params;
};

// 应用参数到目标
// 优化后的 applyParamsToTarget 函数，集成了初级调整颜色的逻辑
_root.applyParamsToTarget = function(target:MovieClip, params):Void {
    if (!target) {
        trace("目标 MovieClip 未定义");
        return;
    }

    // 如果参数是 ColorTransform 对象，直接应用并返回
    if (params instanceof ColorTransform) {
        target.transform.colorTransform = params;
        return;
    }

    // 如果没有传入任何参数，移除当前的 ColorTransform
    if (!params) {
        target.transform.colorTransform = new ColorTransform();
        return;
    }

    // 创建一个新的 ColorTransform 对象
    var colorTransform:ColorTransform = new ColorTransform();

    // 解析基础颜色乘数和偏移
    colorTransform.redMultiplier = params.hasOwnProperty('redMultiplier') ? params['redMultiplier'] : 1;
    colorTransform.greenMultiplier = params.hasOwnProperty('greenMultiplier') ? params['greenMultiplier'] : 1;
    colorTransform.blueMultiplier = params.hasOwnProperty('blueMultiplier') ? params['blueMultiplier'] : 1;
    colorTransform.alphaMultiplier = params.hasOwnProperty('alphaMultiplier') ? params['alphaMultiplier'] : 1;

    colorTransform.redOffset = params.hasOwnProperty('redOffset') ? params['redOffset'] : 0;
    colorTransform.greenOffset = params.hasOwnProperty('greenOffset') ? params['greenOffset'] : 0;
    colorTransform.blueOffset = params.hasOwnProperty('blueOffset') ? params['blueOffset'] : 0;
    colorTransform.alphaOffset = params.hasOwnProperty('alphaOffset') ? params['alphaOffset'] : 0;

    // 高级调整参数
    var brightness:Number = params.hasOwnProperty('brightness') ? params['brightness'] : 0;
    var contrast:Number = params.hasOwnProperty('contrast') ? params['contrast'] : 0;
    var saturation:Number = params.hasOwnProperty('saturation') ? params['saturation'] : 0;
    var hue:Number = params.hasOwnProperty('hue') ? params['hue'] : 0;

    // 色相调整：冷暖（正值增加红色，负值增加蓝绿色）
    if (hue !== 0) {
        // 色相旋转，将色相值限制在0-360度范围内
        var newHue:Number = (colorTransform.redOffset + hue) % 360;
        if (newHue < 0) newHue += 360; // 如果新色相为负值，转换为正值

        // 计算色相差值
        var hueDifference:Number = newHue - colorTransform.redOffset;
        if (hueDifference > 180) hueDifference -= 360; // 如果差值大于180度，则减去360度以找到最短路径
        if (hueDifference < -180) hueDifference += 360; // 如果差值小于-180度，则加上360度以找到最短路径

        // 映射色相差值到RGB偏移
        // 假设整个色环可以映射到255的RGB值范围内
        var rgbDifference:Number = hueDifference / 360 * 255 / 3;

        // 更新RGB偏移值以模拟色相旋转
        colorTransform.redOffset += rgbDifference;
        colorTransform.greenOffset -= rgbDifference / 2;
        colorTransform.blueOffset -= rgbDifference / 2;
    }

    // 对比度调整
    if (contrast !== 0) {
        var contrastMultiplier:Number = 1 + contrast / 100;
        var contrastOffset:Number = 128 * (1 - contrastMultiplier);

        // 应用对比度乘数和偏移
        colorTransform.redMultiplier *= contrastMultiplier;
        colorTransform.greenMultiplier *= contrastMultiplier;
        colorTransform.blueMultiplier *= contrastMultiplier;

        colorTransform.redOffset += contrastOffset;
        colorTransform.greenOffset += contrastOffset;
        colorTransform.blueOffset += contrastOffset;
    }

    // 亮度调整
    if (brightness !== 0) {
        colorTransform.redOffset += brightness;
        colorTransform.greenOffset += brightness;
        colorTransform.blueOffset += brightness;
    }

    // 饱和度调整
    if (saturation !== 0) {
        // 计算灰度平均乘数
        var grayMultiplier:Number = (colorTransform.redMultiplier + colorTransform.greenMultiplier + colorTransform.blueMultiplier) / 3;

        // 调整每个颜色通道的乘数以增加或减少饱和度
        colorTransform.redMultiplier = grayMultiplier + (colorTransform.redMultiplier - grayMultiplier) * (1 + saturation / 100);
        colorTransform.greenMultiplier = grayMultiplier + (colorTransform.greenMultiplier - grayMultiplier) * (1 + saturation / 100);
        colorTransform.blueMultiplier = grayMultiplier + (colorTransform.blueMultiplier - grayMultiplier) * (1 + saturation / 100);
    }

    // 应用修改后的 ColorTransform
    target.transform.colorTransform = colorTransform;
};

// 矩阵相乘函数
_root.multiplyMatrices = function(matrix1:Array, matrix2:Array):Array {
    var result:Array = [];
    for (var i:Number = 0; i < 4; i++) {
        for (var j:Number = 0; j < 5; j++) {
            var value:Number = 0;
            for (var k:Number = 0; k < 4; k++) {
                value += matrix1[i * 5 + k] * matrix2[k * 5 + j];
            }
            if (j == 4) {
                value += matrix1[i * 5 + 4];
            }
            result[i * 5 + j] = value;
        }
    }
    return result;
};

// 生成所有预设的 XML
_root.generateAllPresetsXML = function():String {
    var xml:String = "<PresetSet>\n    <name>" + 预设名.text + "</name>\n";

    for (var i:Number = 0; i < TOTAL_CONTAINERS; i++) {
        var containerName:String = "容器" + i;
        var container:MovieClip = _root[containerName];
        var presetXML:String = _root.generatePresetXML(container, i);

        if (presetXML != "") {
            // 缩进每个预设的 XML 以提高可读性
            var indentedXML:String = presetXML.split("\n").join("\n    ");
            xml += "    " + indentedXML + "\n";
        }
    }

    xml += "  </PresetSet>\n";
    return xml;
};

// 可选地，生成所有预设的 XML
var allPresetsXML:String = _root.generateAllPresetsXML();
trace(allPresetsXML);

stop();
